m: -30px;}.mb-30 {  margin-bottom: 30px;}.faq-footer {  padding: 30px 0;  background-color: #f3f3f3;  border-top: 1px solid #e5e5e5;}.faq-header {  padding: 15px 0;  background-color: #FEEE00;}.faq-logo img {  max-width: 175px;  max-height: 55px;}.faq-header .back-text {  color: #404553;}@media (max-width: 991px) {  .faq-banner-title {    font-size: 42px;  }}@media (max-width: 575px) {  .faq-banner-title {    font-size: 36px;  }}@media (max-width: 480px) {  .faq-banner-title {    font-size: 32px;  }  .faq-box {    padding: 20px;  }  .faq-box .content {    width: 100%;    padding-left: 0;    margin-top: 20px;  }  .faq-box .content .title {    font-size: 20px;  }}</style></head><body>  <header class="faq-header">    <div class="container-fluid">      <div class="row justify-content-between align-items-center">        <div class="col-lg-6">          <a href="#0" class="faq-logo">            <img src="https://www.hypershop.com.bd/public/uploads/all/lNI91I49OxvIhowjkb8kAOgbEORVqJ8uxLIaMdvs.png" alt="image">          </a>        </div>        <div class="col-lg-6 text-right">          <a href="#0" class="back-text">Back to Hypershop</a>        </div>      </div>    </div>  </header>  <section class="faq-banner" style="background-image: url('https://theme.zdassets.com/theme_assets/1061178/469d041ea674c50c3bfc90582ca36ead6884e485.svg')">    <div class="container">      <div class="row justify-content-center">        <div class="col-lg-6">          <h2 class="faq-banner-title">Hello! How can we help?</h2>          <form class="faq-form mt-4">            <input type="text" name="#0" placeholder="Search the help here">            <i class="las la-search"></i>          </form>        </div>      </div>    </div>  </section>    <section class="section-padding">    <div class="container custom-container">      <div class="row mb-none-30">        <div class="col-lg-12">          <h2 class="mb